$.fn.replicant=function(initialData,secondData){var instance=$(this).data("replicantInstance");switch(initialData){case"replicate":return instance.replicate(secondData,false);case"clear":return instance.clear();}if(instance){return;}var model=$(this);model=$(model);var self=this;this.totalItems=0;this.model=$(".replicant-base",model).prop("outerHTML");this.container=model;this.qtd=1;this.clear=function(){this.container.html(this.model);$(".replicant-remove",this.container).hide();this.qtd=1;};$(".replicant-remove",model).hide();model.delegate(".replicant-add","click",function(){self.replicate();});model.delegate(".replicant-clear","click",function(){self.clear();});model.delegate(".replicant-remove","click",function(){self.qtd--;$(this).closest(".replicant-base").remove();});if(initialData&&initialData.onRender){initialData.onRender(model);}model.data("replicantInstance",this);this.replicate=function(value,ignoreAppend){if(initialData&&initialData.onBeforeRender&&!initialData.onBeforeRender(value,self.qtd)){return;}if(!ignoreAppend){this.container.append(this.model);}self.qtd++;$(".replicant-remove",this.container).show();$(".replicant-remove:last",this.container).hide();$(".replicant-add",this.container).not(":last").hide();$(".replicant-clear",this.container).not(":last").hide();if(value){for(var field in value){$('[name="'+field+'"]',$(".replicant-base:last",this.container)).val(value[field]);console.log("campo: ",field,value[field],$('[name="'+field+'"]',$(".replicant-base",this.container)),$('[name="'+field+'"]',$(".replicant-base",this.container)).length);}}if(initialData&&initialData.onRender){initialData.onRender($(".replicant-base:last",this.container));}};if(initialData&&initialData.values){for(var x=0;x<initialData.values.length;x++){this.replicate(initialData.values[x],(x==0));}}};