$.fn.replicant=function(){var model=$(this);model=$(model);var self=this;this.totalItems=0;this.model=$(".replicant-base",model).prop("outerHTML");this.container=model;$(".replicant-remove",model).hide();model.delegate(".replicant-add","click",function(){self.replicate();});model.delegate(".replicant-remove","click",function(){$(this).closest(".replicant-base").remove();});this.replicate=function(){console.log("base: ",this.model);this.container.append(this.model);$(".replicant-remove",this.container).show();$(".replicant-remove:last",this.container).hide();$(".replicant-add",this.container).not(":last").hide();};};